INSERT INTO `lab_news` VALUES ('77', '我校获批12个省工程技术研究中心  ', '<p style=\"text-align: center;\"><strong><span style=\"font-size: 20px;\">我校获批12个省工程技术研究中心 <br/></span></strong></p><p><strong><span style=\"font-size: 20px;\"><span style=\"line-height: 175%;font-size: 16px\"><br/></span></span></strong></p><p><strong><span style=\"font-size: 20px;\"><span style=\"line-height: 175%;font-size: 16px\">&nbsp;&nbsp;&nbsp; </span></span></strong><span style=\"font-size: 20px;\"><span style=\"line-height: 175%;font-size: 16px\">根据《广东省科学技术厅关于认定2015年广东省工程技术研究中心的通知 &nbsp;》(粤科函产学研字〔2015〕1487号)文件精神，我校组织申报的广东省农业养殖物联网等12个工程技术研究中心通过了专家评审和网上公示，现认定为 2015年广东省工程技术研究中心（广东高校共获批84家）；至此，目前我校已建有25个广东省工程技术研究中心。</span></span></p><p><span style=\"line-height: 175%;font-size: 16px\">　　工程技术研究中心是协同创新的重要载体，能够促进产学研结合，提高科技成果的成熟度、配套性和工程化水平，带动相关行业或领<span style=\"line-height: 175%;font-size: 16px\">域的技术进步，提高自主创新能力和市场竞争力，为调整优化经济结构、转变经济发展方式和建设创新型广东具有重要意义。(文/重点实验室建设办公室 夏斌）</span></span></p><p><span style=\"line-height: 175%;font-size: 16px;font-family:宋体\">附：我校2015年获得认定的工程中心清单</span><span style=\"font-family: 仿宋_GB2312;font-size: 19px\">&nbsp;</span></p><p>&nbsp;</p><table cellpadding=\"0\" cellspacing=\"0\"><tbody><tr class=\"firstRow\"><td style=\"border-width: 1px; border-style: solid; border-color: windowtext; padding: 0px 7px;\" width=\"68\"><p style=\"text-align: center\"><strong><span style=\"font-family: 仿宋_GB2312;font-size: 19px\">序号</span></strong></p></td><td style=\"border-width: 1px 1px 1px medium; border-style: solid solid solid none; border-color: windowtext windowtext windowtext -moz-use-text-color; padding: 0px 7px;\" width=\"235\"><p style=\"text-align: center\"><strong><span style=\"font-family: 仿宋_GB2312;font-size: 19px\">中心名称</span></strong></p></td><td style=\"border-width: 1px 1px 1px medium; border-style: solid solid solid none; border-color: windowtext windowtext windowtext -moz-use-text-color; padding: 0px 7px;\" width=\"123\"><p style=\"text-align: center\"><strong><span style=\"font-family: 仿宋_GB2312;font-size: 19px\">中心负责人</span></strong></p></td><td style=\"border-width: 1px 1px 1px medium; border-style: solid solid solid none; border-color: windowtext windowtext windowtext -moz-use-text-color; padding: 0px 7px;\" width=\"163\"><p style=\"text-align: center\"><strong><span style=\"font-family: 仿宋_GB2312;font-size: 19px\">依托单位</span></strong></p></td></tr><tr><td style=\"border-width: medium 1px 1px; border-style: none solid solid; border-color: -moz-use-text-color windowtext windowtext; padding: 0px 7px;\" width=\"68\"><p style=\"text-align: center\"><strong><span style=\"font-family: 仿宋_GB2312;font-size: 19px;font-weight: normal\">1</span></strong></p></td><td style=\"border-width: medium 1px 1px medium; border-style: none solid solid none; border-color: -moz-use-text-color windowtext windowtext -moz-use-text-color; padding: 0px 7px;\" width=\"235\"><p style=\"text-align: center\"><strong><span style=\"font-family: 仿宋_GB2312;font-size: 19px;font-weight: normal\">广东省农业养殖物联网工程技术研究中心</span></strong></p></td><td style=\"border-width: medium 1px 1px medium; border-style: none solid solid none; border-color: -moz-use-text-color windowtext windowtext -moz-use-text-color; padding: 0px 7px;\" width=\"123\"><p style=\"text-align: center\"><strong><span style=\"font-family: 仿宋_GB2312;font-size: 19px;font-weight: normal\">刘财兴</span></strong></p></td><td style=\"border-width: medium 1px 1px medium; border-style: none solid solid none; border-color: -moz-use-text-color windowtext windowtext -moz-use-text-color; padding: 0px 7px;\" width=\"163\"><p style=\"text-align: center\"><strong><span style=\"font-family: 仿宋_GB2312;font-size: 19px;font-weight: normal\">数学与信息学院</span></strong></p></td></tr><tr><td style=\"border-width: medium 1px 1px; border-style: none solid solid; border-color: -moz-use-text-color windowtext windowtext; padding: 0px 7px;\" width=\"68\"><p style=\"text-align: center\"><strong><span style=\"font-family: 仿宋_GB2312;font-size: 19px;font