INSERT INTO `users` VALUES ('3', 'wangmin', 'wangmin@163.com', '$2y$10$3N1oI.KSHKeqQ5ePYZVYWedTwa6QWguwbNWtldbfmpj2c2gc..F0W', '2', '1', null, '2017-11-15 06:51:46', '2018-01-09 09:11:39');
