function addLink(){$.ajax({type:"GET",url:$.GetUrl()+"LinkAction/addLink",data:{name:$("#link_name").val(),link:$("#link_address").val(),parent:$("#select_category option:selected").val()},dataType:"json",success:function(n){"1"==n&&(toastr.success("新增成功"),setTimeout(function(){window.location.reload()},800))},error:function(){toastr.error("连接失败")}})}jQuery.GetUrl=function(){var n=window.location.pathname.split("/");return"static"==n[1]||n[1].indexOf("Action")>0?"/":"/"+n[1]+"/"},$(function(){$("#link_name").focus(),$("#addLinkForm").validate({submitHandler:function(n){addLink()}}),jQuery.extend(jQuery.validator.messages,{required:"必选字段",url:"请输入合法的网址"})});